 &lt;url name="forum_Paged"location = "forums"path = "{0}/{1}/ShowForum.aspx"pattern = "(\d+)/(\d+)/ShowForum.aspx"vanity="ShowForum.aspx?PageIndex=$1^ForumID=$2"/&gt;  然后siteUrl.cs内增加
public virtual string FormPagedFormat (int forumID)
{
return urlData.FormatUrl("forum_Paged", "{0}", forumID.ToString(), forumID.ToString());
